mapper.insert:
		
			SELECT  IFNULL(MAX(SN)+1, 1) AS	sn
			  FROM  LETTCZIP
	    

		
			INSERT 		/* ZipManageDAO.insertZip */    
			  INTO  LETTCZIP
			     (  ZIP         
			     ,  SN          
			     ,  CTPRVN_NM   
			     ,  SIGNGU_NM   
			     ,  EMD_NM      
			     ,  LI_BULD_NM  
			     ,  LNBR_DONG_HO
			     ,  FRST_REGIST_PNTTM
			     ,  FRST_REGISTER_ID
			     ,  LAST_UPDT_PNTTM
			     ,  LAST_UPDUSR_ID
			     ) 
			VALUES
			     (  #{zip}       
			     ,  #{sn}        
			     ,  #{ctprvnNm}  
			     ,  #{signguNm}  
			     ,  #{emdNm}     
			     ,  #{liBuldNm} 
			     ,  #{lnbrDongHo}
			     ,  SYSDATE()
			     ,  #{frstRegisterId}
			     ,  SYSDATE()
			     ,  #{frstRegisterId}
			     )


	INSERT 		/* ZipManageDAO.insertExcelZip */    
	  INTO  LETTCZIP
	     (  ZIP         
	     ,  SN          
	     ,  CTPRVN_NM   
	     ,  SIGNGU_NM   
	     ,  EMD_NM      
	     ,  LI_BULD_NM  
	     ,  LNBR_DONG_HO
	     ,  FRST_REGIST_PNTTM
	     ,  FRST_REGISTER_ID
	     ,  LAST_UPDT_PNTTM
	     ,  LAST_UPDUSR_ID
	     ) 
	VALUES
	     (  #{zip}       
	     ,  #{sn}        
	     ,  #{ctprvnNm}  
	     ,  #{signguNm}  
	     ,  #{emdNm}     
	     ,  #{liBuldNm} 
	     ,  #{lnbrDongHo}
	     ,  SYSDATE()
	     ,  #{frstRegisterId}
	     ,  SYSDATE()
	     ,  #{frstRegisterId}
	     )

mapper.update:
		
            UPDATE		/* ZipManageDAO.updateZip */      
            LETTCZIP 
               SET  CTPRVN_NM         = #{ctprvnNm}  
                 ,  SIGNGU_NM         = #{signguNm}  
                 ,  EMD_NM            = #{emdNm}     
                 ,  LI_BULD_NM        = #{liBuldNm} 
                 ,  LNBR_DONG_HO      = #{lnbrDongHo}
                 ,  LAST_UPDT_PNTTM = sysdate()
                 ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
             WHERE  ZIP               = #{zip}
               AND  SN                = #{sn}

mapper.delete:
		
            DELETE  /* ZipManageDAO.deleteZip */      
              FROM  LETTCZIP     
             WHERE  ZIP = #{zip}
