mapper.insert:

	INSERT INTO lettnprogrmlist 	/* progrmManageDAO.insertProgrm_S */      
		( PROGRM_FILE_NM
		  , PROGRM_STRE_PATH
		  , PROGRM_KOREAN_NM
		  , PROGRM_DC
		  , URL )
	VALUES ( #{progrmFileNm}
		  , #{progrmStrePath}
		  , #{progrmKoreanNm}
		  , #{progrmDc}
		  , #{URL} )

mapper.update:

	UPDATE lettnprogrmlist 		/* progrmManageDAO.updateProgrm_S */      
	SET PROGRM_STRE_PATH=#{progrmStrePath}
		, PROGRM_KOREAN_NM=#{progrmKoreanNm}
		, PROGRM_DC      =#{progrmDc}
		, URL            =#{URL}
	WHERE PROGRM_FILE_NM=#{progrmFileNm}
