mapper.select:
SELECT /*groupManageDAO.selectGroupList*/ GROUP_ID , GROUP_NM , GROUP_DC , GROUP_CREAT_DE FROM LETTNAUTHORGROUPINFO WHERE 1=1
AND ( GROUP_ID LIKE CONCAT('%' , #{searchKeyword}, '%') OR GROUP_NM LIKE CONCAT('%' , #{searchKeyword}, '%') OR GROUP_DC LIKE CONCAT('%' , #{searchKeyword}, '%') )
ORDER BY GROUP_CREAT_DE DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

            SELECT /*groupManageDAO.selectJqGroupList*/
            	  GROUP_ID
            	, GROUP_NM
            	, GROUP_DC
            	, GROUP_CREAT_DE
              FROM 
              	LETTNAUTHORGROUPINFO
             WHERE 
             	1=1


             		AND ${searchCnd} LIKE CONCAT ('%', #{searchWrd},'%')



						AND( GROUP_ID LIKE CONCAT ('%', #{searchWrd},'%')
							OR GROUP_NM LIKE CONCAT ('%', #{searchWrd},'%')
							OR GROUP_DC LIKE CONCAT ('%', #{searchWrd},'%')	)             		



            ORDER BY 

            	GROUP_ID ${sord}


            	GROUP_NM ${sord}


            	GROUP_DC ${sord}


            	GROUP_CREAT_DE ${sord}

            LIMIT #{rows} 
            OFFSET #{firstIndex}

mapper.insert:
	INSERT /*groupManageDAO.insertGroup*/
	INTO LETTNAUTHORGROUPINFO 
		  ( GROUP_ID
		  , GROUP_NM
		  , GROUP_DC
		  , GROUP_CREAT_DE )
   VALUES ( #{groupId}
	      , #{groupNm}
		  , #{groupDc}
		  , now())

mapper.update:
	UPDATE /*groupManageDAO.updateGroup*/
		LETTNAUTHORGROUPINFO 
	SET 
		   GROUP_NM=#{groupNm}
		 , GROUP_DC=#{groupDc}
		 , GROUP_CREAT_DE=now()
  	WHERE 
  		GROUP_ID=#{groupId}

	UPDATE /*groupManageDAO.updateJqGroup*/
		LETTNAUTHORGROUPINFO 


			GROUP_NM=#{cellValue}


			GROUP_DC=#{cellValue}


  	WHERE 
  		GROUP_ID=#{id}
