mapper.insert:
		
			INSERT INTO LETTNMENUINFO 		/* menuManageDAO.insertMenuManage_S */     
				( MENU_NO
            , MENU_ORDR
            , MENU_NM
            , UPPER_MENU_NO
            , MENU_DC
            , PROGRM_FILE_NM
            , USE_YN )
			VALUES (   #{menuNo}
                  , #{menuOrdr}
                  , #{menuNm}  
                  , #{upperMenuId}
                  , #{menuDc}
                  , #{progrmFileNm} 
                  , #{useYn})

mapper.update:
		
		
			UPDATE LETTNMENUINFO 		/* menuManageDAO.updateMenuManage_S */     
			SET  MENU_ORDR         = #{menuOrdr}       
            , MENU_NM           = #{menuNm}         
            , UPPER_MENU_NO     = #{upperMenuId}    
            , MENU_DC           = #{menuDc}         
            , PROGRM_FILE_NM    = #{progrmFileNm}    
            , USE_YN			= #{useYn}
			WHERE MENU_NO=#{menuNo}
