mapper.select:
SELECT /* CommCodeMngDAO.selectCommCodeList */ B.CL_CODE_NM , A.CODE_ID , A.CODE_ID_NM , A.USE_AT , B.CL_CODE FROM LETTCCMMNCODE A , LETTCCMMNCLCODE B WHERE A.CL_CODE = B.CL_CODE
AND A.CODE_ID = #{searchKeyword}
AND A.CODE_ID_NM LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.CL_CODE = #{searchKeyword}
AND A.USE_AT = 'Y'
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT /* CommCodeMngDAO.selectCommCodeListTotCnt */ COUNT(*) totcnt FROM LETTCCMMNCODE A , LETTCCMMNCLCODE B WHERE A.CL_CODE = B.CL_CODE
AND CODE_ID = #{searchKeyword}
AND CODE_ID_NM LIKE CONCAT('%', #{searchKeyword}, '%')

mapper.insert:

    INSERT /* CommCodeMngDAO.insertCommCode */     
      INTO  LETTCCMMNCODE
         (  CODE_ID
         ,  CODE_ID_NM
         ,  CODE_ID_DC
         ,  CL_CODE
         ,  USE_AT
         ,  FRST_REGIST_PNTTM
         ,  FRST_REGISTER_ID
         ,  LAST_UPDT_PNTTM
         ,  LAST_UPDUSR_ID
         ) 
    VALUES  
         (  #{codeId}
         ,  #{codeIdNm}
         ,  #{codeIdDc}
         ,  #{clCode}
         ,  #{useAt}
         ,  sysdate()
         ,  #{frstRegisterId}
         ,  sysdate()
         ,  #{frstRegisterId}
         )

mapper.update:
		
            UPDATE	/* CommCodeMngDAO.updateCommCode */       
            LETTCCMMNCODE 
               SET  CODE_ID_NM        = #{codeIdNm}
                 ,  CODE_ID_DC        = #{codeIdDc}
                 ,  USE_AT            = #{useAt}
                 ,  LAST_UPDT_PNTTM = sysdate()
                 ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
             WHERE  CODE_ID           = #{codeId}
